void
Skeleton::SetupReadout(CExperiment* pExperiment)
{
  CReadoutMain::SetupReadout(pExperiment);

  // Create an event segment for each board:
  CompassEventSegment* pBoard1 =                           
    new CompassEventSegment("./compass-settings.xml", // Compass config file.
                            1,                     // Module evb source id.
                            CAEN_DGTZ_USB,
                            0, 0, 0x22220000, "cheats.txt");

  CompassMultiModuleEventSegment *mainSegment =           
    new CompassMultiModuleEventSegment;

  mainSegment->addModule(pBoard1);                     



  // Establish your trigger here by creating a trigger object
  // and establishing it.

  // The trigger needs to know about all modules too:

  CompassTrigger* pTrigger = new CompassTrigger;        
  pTrigger->addModule(pBoard1);                      

  // Create and add your event segments here, by creating them and invoking CExperiment's
  // AddEventSegment

  pExperiment->AddEventSegment(mainSegment);         
  pExperiment->EstablishTrigger(pTrigger);           


}

void
Skeleton::SetupReadout(CExperiment* pExperiment)
{
  CReadoutMain::SetupReadout(pExperiment);

  CDPpPsdEventSegment* pSegment =                
    new CDPpPsdEventSegment(PSDBoardParameters::usb, 0, 0, 0x33330000, 1, "../settings.xml");
  CPsdCompoundEventSegment*  pCompound = new CPsdCompoundEventSegment(); 
  pCompound->addModule(pSegment);               

  pExperiment->AddEventSegment(pCompound);      

  CPsdTrigger* pTrigger = new CPsdTrigger;      
  pTrigger->addModule(pSegment);                
  pExperiment->EstablishTrigger(pTrigger);      

}

void
Skeleton::SetupReadout(CExperiment* pExperiment)
{
  CReadoutMain::SetupReadout(pExperiment);
                                          
  CDPpPsdEventSegment* pPsdBoard =
    new CDPpPsdEventSegment(PSDBoardParameters::usb, 0, 0, 0x33330000, 1, "../settings.xml");
  CPsdCompoundEventSegment*  pPsd = new CPsdCompoundEventSegment();
  pPsd->addModule(pPsdBoard);

                                         
  CompassEventSegment* pPhaBoard = 
    new CompassEventSegment("./settings.xml", // Compass config file.
			    1,                     // Module evb source id.
			    CAEN_DGTZ_USB, 
			    0, 0, 0x22220000);
  
  CompassMultiModuleEventSegment *pPha = 
    new CompassMultiModuleEventSegment;

  pPha->addModule(pPhaBoard);

  COneOnlyEventSegment* pCombined = new COneOnlyEventSegment; 
  pCombined->addModule(pPsd);                              
  pCombined->addModule(pPha);                              

  pExperiment->AddEventSegment(pCombined);                

  CPsdTrigger* pPsdTrigger = new CPsdTrigger;                
  pPsdTrigger->addModule(pPsdBoard);
  CompassTrigger* pPhaTrigger = new CompassTrigger;          
  pPhaTrigger->addModule(pPhaBoard);

  CCompoundTrigger* pTrigger = new CCompoundTrigger;       
  pTrigger->addTrigger(pPsdTrigger);                    
  pTrigger->addTrigger(pPhaTrigger);                    

  pExperiment->EstablishTrigger(pTrigger);              
  
}

