#include <LogBookShift.h>

class LogBookShift
{
public:
    int                               id() const;
    const char*                       name() const;
    const std::vector<LogBookPerson*>& members() const;

    void addMember(CSqlite& db, LogBookPerson* person);
    void removeMember(CSqlite& db, LogBookPerson* person);
    void setCurrent(CSqlite& db);

    static LogBookShift* create(
        CSqlite& db,
        const char* shiftName, const std::vector<LogBookPerson*>& people
    );
    static LogBookShift* create(CSqlite& db, const char* shiftName);
    static std::vector<LogBookShift*> list(CSqlite& db);
    static LogBookShift* find(CSqlite& db, const char* name);
    static LogBookShift* getCurrent(CSqlite& db);
};

class LogBookRun {
public:
    typedef struct _RunInfo {
        int         s_id;
        int         s_number;
        std::string s_title;
    } RunInfo, *pRunInfo;
    typedef struct _Transition {
        int         s_id;
        int         s_transition;
        std::string s_transitionName;
        int         s_transitionTime;
        std::string s_transitionComment;
        LogBookShift* s_onDuty;
    } Transition, *pTransition;
    typedef struct _Run {
        RunInfo                 s_Info;
        std::vector<Transition> s_transitions;
    } Run, *pRun;
public:
    LogBookRun(CSqlite& db, int id);

    const RunInfo& getRunInfo() const;
    bool  isCurrent(CSqlite& db) const;
    const Transition& operator[](int n) const;
    size_t numTransitions() const;
    int    lastTransitionType() const;
    const char* lastTransition() const;
    bool isActive() const;
    void transition(CSqlite& db, const char* type, const char* note);


    //
    static LogBookRun* currentRun(CSqlite& db);
    static int runId(CSqlite& db, int runNumber);
    static int begin(
        CSqlite& db,  int number,
        const char* title, const char* remark=nullptr
    );
    static void  end (
        CSqlite& db,  int runid,
        const char* remark=nullptr
    );
    static void  pause (
        CSqlite& db,  int runid,
        const char* remark=nullptr
    );
    static void  resume (
        CSqlite& db,  int runid,
        const char* remark=nullptr
    );
    static void  emergency_end (
        CSqlite& db,  int runid,
        const char* remark
    );
    static std::vector<LogBookRun*> list(CSqlite& db);
    static LogBookRun* find(CSqlite& db, int runNumber);


    static bool isLegal(CSqlite& db, int runId, int proposedTransition);


};

#include <LogBookNote.h>

class LogBookNote
{
public:
    typedef struct _NoteText {
        int        s_id;
        int        s_runId;     // Associated run.
        int        s_authorId;
        time_t     s_noteTime;
        std::string s_contents;
    } NoteText, *pNoteText;
    typedef struct _ImageInfo {
        std::string s_filename;
        size_t      s_offset;
    } ImageInfo, *pImageInfo;
    typedef struct _NoteImage {
        int         s_id;
        int         s_noteId;
        int         s_noteOffset;
        std::string s_originalFilename;
        size_t      s_imageLength;
        void*       s_pImageData;
    } NoteImage, *pNoteImage;
public:
    LogBookNote(CSqlite& db, int noteId);

    LogBookRun* getAssociatedRun(CSqlite& db) const;
    const NoteText& getNoteText() const;
    size_t          imageCount() const;
    const NoteImage& operator[](int n) const;
    std::string substituteImages();

    static LogBookNote* create(
        CSqlite& db, LogBookRun* run, const char* string,
        LogBookPerson* pPerson,
        const std::vector<ImageInfo>& images
    );

    static std::vector<int> listRunNoteIds(CSqlite& db, int runId);
    static std::vector<int> listNonRunNotes(CSqlite& db);
    static std::vector<LogBookNote*> getAllNotes(CSqlite& db);
    static std::vector<LogBookNote*> getRunNotes(CSqlite& db, int runId);
    static std::vector<LogBookNote*> getNonRunNotes(CSqlite& db);
    std::string exportImage(const NoteImage& image);

};

listitem.para:
Not for general use.  Given a logbook database;
db returns the ids of all notes
that are not associated with any run.

cmdsynopsis.command:
if {[$run isActive]} {
   puts "The run is active"
}

informalexample.programlisting:
set current [currentRun]
if {[dict size $current] != 0} {
   puts "There is a current run; number [dict get $current number]" 
} else {
   puts "There is not a currently active run."
}

